style.css:
@-moz-document domain("chatgpt.com"), domain("chat.openai.com") {
:root {
  --background-color: #2e3440;
  --strong-color: #ebcb8b;
  --em-color: #88c0d0;
  --h1-color: #bf616a;
  --h2-color: #a3be8c;
  --h3-color: #b48ead;
  --h4-color: #b48ead;
  --h5-color: #8fbcbb;
  --h6-color: #ebcb8b;
}

body {
  background-color: var(--background-color, /*[[background-color]]*/) !important;
}

/* General styles for strong and em */
strong {
  color: var(--strong-color, /*[[strong-color]]*/) !important;
}

em {
  color: var(--em-color, /*[[em-color]]*/) !important;
}

/* Specific styles for headings */
h1 strong, h1 em,
h2 strong, h2 em,
h3 strong, h3 em,
h4 strong, h4 em,
h5 strong, h5 em,
h6 strong, h6 em {
  color: inherit !important; /* or set to a specific color for headings */
}

/* Specific colors for headings */
h1 {
  color: var(--h1-color, /*[[h1-color]]*/) !important;
}

h2 {
  color: var(--h2-color, /*[[h2-color]]*/) !important;
}

h3 {
  color: var(--h3-color, /*[[h3-color]]*/) !important;
}

h4 {
  color: var(--h4-color, /*[[h4-color]]*/) !important;
}

h5 {
  color: var(--h5-color, /*[[h5-color]]*/) !important;
}

h6 {
  color: var(--h6-color, /*[[h6-color]]*/) !important;
}
}

options.value:
