  --background-color: 'add color here';
  --strong-color: 'add color here';
  --em-color: 'add color here';
  --h1-color: 'add color here';
  --h2-color: 'add color here';
  --h3-color: 'add color here';
  --h4-color: 'add color here';
  --h5-color: 'add color here';
  --h6-color: 'add color here';
}
