style.css:
@-moz-document domain("youtube.com") {
/* Main background */
    body, 
    #page-manager,
    ytd-app {
        background-color: #0a0a0a !important;
    }

    /* Text colors */
    body {
        color: #ffffff !important;
    }

    /* Secondary text color */
    .ytd-video-meta-block,
    #description,
    .ytd-comment-renderer,
    .ytd-video-secondary-info-renderer {
        color: #ff4500 !important;
    }

    /* Add a dynamic background */
    body::before {
        content: "";
        position: fixed;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background: 
            linear-gradient(45deg, #1a0000 25%, transparent 25%),
            linear-gradient(-45deg, #1a0000 25%, transparent 25%),
            linear-gradient(45deg, transparent 75%, #1a0000 75%),
            linear-gradient(-45deg, transparent 75%, #1a0000 75%);
        background-size: 20px 20px;
        animation: backgroundMove 10s linear infinite;
        opacity: 0.1;
        z-index: -1;
    }

    @keyframes backgroundMove {
        0% {background-position: 0 0, 10px 0, 10px -10px, 0px 10px;}
        100% {background-position: 20px 20px, 30px 20px, 30px 10px, 20px 30px;}
    }

    /* Add a pulsating glow effect to elements */
    #content, 
    #related {
        box-shadow: 0 0 15px rgba(255, 69, 0, 0.5);
        animation: pulse 4s infinite alternate;
    }

    @keyframes pulse {
        0% {box-shadow: 0 0 15px rgba(255, 69, 0, 0.5);}
        100% {box-shadow: 0 0 30px rgba(255, 165, 0, 0.8);}
    }

    /* Style the header */
    #masthead {
        background-color: rgba(10, 10, 10, 0.9) !important;
        border-bottom: 2px solid #ff4500;
    }

    /* Style buttons */
    .yt-spec-button-shape-next--filled,
    .ytp-button {
        background-color: #8b0000 !important;
        color: #ffffff !important;
        transition: all 0.3s ease;
    }

    .yt-spec-button-shape-next--filled:hover,
    .ytp-button:hover {
        background-color: #ff4500 !important;
        transform: scale(1.05);
    }

    /* Style video progress bar */
    .ytp-progress-bar-container {
        background-color: #ff4500 !important;
    }

    /* Add a border to thumbnails with hover effect */
    .ytd-thumbnail {
        border: 2px solid #8b0000;
        transition: all 0.3s ease;
    }

    .ytd-thumbnail:hover {
        transform: scale(1.05);
        box-shadow: 0 0 20px #ff4500;
    }

    /* Style comments section */
    #comments {
        background-color: rgba(10, 10, 10, 0.8);
        border-top: 2px solid #8b0000;
        padding: 20px;
    }

    /* Add a custom scrollbar */
    ::-webkit-scrollbar {
        width: 12px;
    }

    ::-webkit-scrollbar-track {
        background: #0a0a0a;
    }

    ::-webkit-scrollbar-thumb {
        background: #8b0000;
        border-radius: 6px;
    }

    ::-webkit-scrollbar-thumb:hover {
        background: #ff4500;
    }

    /* Add floating particles effect */
    body::after {
        content: '';
        position: fixed;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        pointer-events: none;
        background-image: 
            radial-gradient(circle, #ff4500 1px, transparent 1px),
            radial-gradient(circle, #ff8c00 1px, transparent 1px);
        background-size: 40px 40px;
        background-position: 0 0, 20px 20px;
        animation: particlesMove 20s linear infinite;
        opacity: 0.1;
        z-index: -1;
    }

    @keyframes particlesMove {
        0% {transform: translateY(0);}
        100% {transform: translateY(-40px);}
    }
}
