style.css:
@-moz-document domain("online.academyart.edu") {
/* Earthy green background */
  body {
    background-color: #556B2F !important; /* Earthy green */
    color: #ffffff !important;
  }

  /* Global font style for a Studio Ghibli vibe */
  * {
    font-family: "Ghibli Inspired", sans-serif !important; /* Custom whimsical font */
  }

  /* Customize links for a softer feel */
  a {
    color: #87CEEB !important; /* Soft sky blue */
    text-decoration: none !important;
  }

  a:hover {
    text-decoration: underline dashed !important;
  }

  /* Style headers with a whimsical, earthy tone */
  h1, h2, h3, h4, h5, h6 {
    color: #fffdd0 !important; /* Pale cream for titles */
    font-weight: bold !important;
  }

  /* Customize buttons with a natural feel */
  button, .btn {
    background-color: #8FBC8F !important; /* Soft green */
    color: #ffffff !important;
    border-radius: 12px !important;
    border: 2px solid #556B2F !important;
    box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.2) !important;
  }

  /* Style the navbar with a natural green */
  .navbar {
    background-color: #2F4F4F !important; /* Dark slate green */
    border-bottom: 4px solid #8FBC8F !important; /* Light green border */
  }

  /* Add Studio Ghibli-themed background image for the page header */
  .header {
    background-image: url('https://i.imgur.com/nTLDBoq.jpg') !important; /* Studio Ghibli background image */
    background-size: cover !important;
    height: 300px !important;
  }

  /* Style for class cards */
  .class-card {
    background-color: rgba(255, 255, 255, 0.9) !important; /* Light background with transparency */
    border-radius: 10px !important;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2) !important;
    padding: 20px !important;
    margin-bottom: 20px !important;
    transition: transform 0.3s ease !important;
  }

  .class-card:hover {
    transform: scale(1.05) !important; /* Slight zoom on hover */
  }

  /* Customize the 4 class cards with Studio Ghibli GIFs */
 
  /* First class card */
  .class-card:nth-child(1) {
    background-image: url('https://i.imgur.com/RDU00ev.gif') !important; /* Ghibli GIF 1 */
    background-size: cover !important;
    background-position: center !important;
  }

  /* Second class card */
  .class-card:nth-child(2) {
    background-image: url('https://i.imgur.com/9gSG6ZE.gif') !important; /* Ghibli GIF 2 */
    background-size: cover !important;
    background-position: center !important;
  }

  /* Third class card */
  .class-card:nth-child(3) {
    background-image: url('https://i.imgur.com/FK6NOZY.gif') !important; /* Ghibli GIF 3 */
    background-size: cover !important;
    background-position: center !important;
  }

  /* Fourth class card */
  .class-card:nth-child(4) {
    background-image: url('https://i.imgur.com/seNXiU7.gif') !important; /* Ghibli GIF 4 */
    background-size: cover !important;
    background-position: center !important;
  }

  /* Make class cards more readable with padding */
  .class-container .class-card {
    padding: 20px !important;
    color: #000 !important; /* Darker text for readability */
  }

  /* Remove ads or popups */
  .ad, .popup {
    display: none !important;
  }
}
