style.css:
@-moz-document domain("claude.ai") {
/* Main chat container */
  .ChatMessageList {
    max-width: 800px !important;
    margin: 0 auto !important;
    font-family: 'Arial', sans-serif !important;
    background-color: #1a1a2e !important;
    color: #e0e0e0 !important;
    border-radius: 10px !important;
    overflow: hidden !important;
    box-shadow: 0 0 20px rgba(0, 0, 0, 0.5) !important;
  }

  /* Chat header */
  .PageWithSidebarLayout_header {
    background-color: #16213e !important;
    padding: 15px !important;
    text-align: center !important;
    font-size: 24px !important;
    font-weight: bold !important;
    color: #00ff9d !important;
  }

  /* Messages container */
  .ChatMessageList {
    padding: 20px !important;
    max-height: 500px !important;
    overflow-y: auto !important;
  }

  /* Individual message */
  .ChatMessage {
    margin-bottom: 15px !important;
    padding: 10px 15px !important;
    border-radius: 20px !important;
    max-width: 80% !important;
  }

  /* Human message */
  .ChatMessage.human {
    background-color: #0f3460 !important;
    align-self: flex-end !important;
    margin-left: auto !important;
  }

  /* AI message */
  .ChatMessage.ai {
    background-color: #2a2a4a !important;
    align-self: flex-start !important;
  }

  /* Input area */
  .ChatInputPanel {
    display: flex !important;
    padding: 15px !important;
    background-color: #16213e !important;
  }

  .ChatInputPanel textarea {
    flex-grow: 1 !important;
    padding: 10px !important;
    border: none !important;
    border-radius: 20px !important;
    background-color: #2a2a4a !important;
    color: #e0e0e0 !important;
    font-size: 16px !important;
  }

  .ChatInputPanel button[type="submit"] {
    background-color: #00ff9d !important;
    color: #1a1a2e !important;
    border: none !important;
    border-radius: 50% !important;
    width: 40px !important;
    height: 40px !important;
    margin-left: 10px !important;
    cursor: pointer !important;
    font-size: 18px !important;
    display: flex !important;
    align-items: center !important;
    justify-content: center !important;
    transition: background-color 0.3s ease !important;
  }

  .ChatInputPanel button[type="submit"]:hover {
    background-color: #00cc7e !important;
  }

  /* Scrollbar styling */
  ::-webkit-scrollbar {
    width: 8px !important;
  }

  ::-webkit-scrollbar-track {
    background: #1a1a2e !important;
  }

  ::-webkit-scrollbar-thumb {
    background: #00ff9d !important;
    border-radius: 4px !important;
  }

  ::-webkit-scrollbar-thumb:hover {
    background: #00cc7e !important;
  }
}
