style.css:
* {
    cursor: none !important; /* Oculta el cursor en toda la página */
}

body {
    user-select: none !important; /* Evita la selección de texto */
}

body::after {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    cursor: default; /* Restaura el cursor estándar después del clic */
}
