style.css:
/* Mahesh Babu Fan Theme for Brave Browser */
body {
    background-color: #1c1c1c; /* Dark background for sleek feel */
    color: #ffffff; /* White text for contrast */
}

a {
    color: #00bfff; /* Mahesh Babu's signature blue color for links */
    text-decoration: none;
}

a:hover {
    color: #ff4500; /* Orange highlight on hover */
}

header, footer, nav {
    background-color: #282828; /* Darker sections for nav, header, and footer */
}

button {
    background-color: #00bfff; /* Blue buttons to match the theme */
    border: none;
    color: white;
    padding: 10px 20px;
    border-radius: 5px;
}

button:hover {
    background-color: #ff4500; /* Hover effect for buttons */
}

h1, h2, h3 {
    font-family: 'Arial', sans-serif;
    color: #00bfff; /* Mahesh Babu's signature blue for headings */
}
