style.css:
@-moz-document domain("web.toddleapp.com"), url-prefix("https://web.toddleapp.com") {
/* Set background color to dark */
  body, .main-container, .page-content {
    background-color: #121212 !important;
    color: #ffffff !important;
  }

  /* Adjust text colors */
  h1, h2, h3, h4, h5, h6, p, span, div, label, input, button {
    color: #e0e0e0 !important;
  }

  /* Customize headers or navbar */
  .header, .navbar, .top-bar {
    background-color: #1c1c1c !important;
    border-color: #333 !important;
  }

  /* Style buttons */
  button, .btn, .submit-btn {
    background-color: #333 !important;
    border: 1px solid #555 !important;
    color: #e0e0e0 !important;
  }

  /* Darken input fields */
  input, textarea, select {
    background-color: #2b2b2b !important;
    color: #ffffff !important;
    border: 1px solid #444 !important;
  }

  /* Adjust links and other interactive elements */
  a, .link {
    color: #00bcd4 !important;
  }

  /* Modify cards, boxes, and panels */
  .card, .box, .panel {
    background-color: #1c1c1c !important;
    border: 1px solid #333 !important;
    color: #e0e0e0 !important;
  }

  /* Optional: Tweak scrollbars */
  ::-webkit-scrollbar {
    width: 10px;
  }
  ::-webkit-scrollbar-thumb {
    background-color: #444;
  }
  ::-webkit-scrollbar-track {
    background-color: #222;
  }

  /* Hide specific bright elements (if needed) */
  .bright-element {
    display: none !important;
  }
}
