style.css:
@media (min-width: 1920px) {
  /* Styles for screens with a minimum width of 1920px */
  body {
    background-color: lightblue;
  }

  /* Adjust any element based on the larger screen size */
  .container {
    max-width: 1920px;
    margin: auto;
  }
}

@media (max-width: 2240px) {
  /* Styles for screens with a maximum width of 2240px (1920 + 320) */
  body {
    background-color: lightgreen;
  }

  /* Adjust any element based on the larger screen size */
  .container {
    max-width: 2240px;
    margin: auto;
  }
}
