style.css:
@-moz-document domain("code.org") {
/* General dark theme for the entire website */
    body {
        background-color: #1e1e1e;
        color: #e0e0e0;
    }

    .header, .footer, .main, .content {
        background-color: #2b2b2b;
        color: #e0e0e0;
    }

    input,
    textarea {
        background-color: #333333;
        color: #ffffff;
        border: 1px solid #555555;
    }

    button,
    .btn,
    .ui-button,
    .dropdown-toggle,
    .btn-primary,
    .btn-secondary {
        background-color: #444444;
        color: #ffffff;
        border: 1px solid #555555;
    }

    /* Darken navigation and toolbar */
    .navbar,
    .menu,
    .nav-item,
    .nav-link,
    .header {
        background-color: #2b2b2b;
        color: #f0f0f0;
    }

    /* Style projects screen */
    .projects-list,
    .project-card,
    .card {
        background-color: #2b2b2b;
        color: #e0e0e0;
        border: 1px solid #444444;
    }

    .projects-list .card-title {
        color: #ffffff;
    }

    /* Game Lab Toolbox (the left-hand blocks area) */
    .toolbox {
        background-color: #2b2b2b !important;
        color: #e0e0e0 !important;
    }

    .toolbox .blocklyTreeLabel {
        color: #ffffff !important;
    }

    .toolbox .blocklyFlyout {
        background-color: #333333 !important;
    }

    .toolbox .blocklyBlockBackground {
        fill: #444444 !important;
    }

    /* Block category buttons */
    .blocklyFlyoutButton > rect {
        fill: #2d2d2d !important;
    }

    .blocklyFlyoutButton > text {
        fill: #e0e0e0 !important;
    }

    /* Make sure all workspace elements are styled */
    .blocklyMainBackground {
        background-color: #333333 !important;
    }

    /* Blocks themselves */
    .blocklyBlockBackground {
        fill: #444444 !important;
    }

    .blocklyText {
        fill: #e0e0e0 !important;
    }

    /* Dark theme for Game Lab code editor */
    .ace_editor, /* Target the ACE editor used in Game Lab */
    .ace_content, 
    .ace_gutter {
        background-color: #2d2d2d;
        color: #e8e8e8;
    }

    .ace_gutter {
        background-color: #2b2b2b;
        color: #888888;
    }

    .ace_cursor {
        color: #ffffff;
    }

    .ace_marker-layer .ace_selection {
        background-color: #666666;
    }

    .ace_marker-layer .ace_active-line {
        background-color: #333333;
    }

    .ace_print-margin {
        background-color: #444444;
    }

    .ace_keyword, .ace_variable {
        color: #9ecdf7;
    }

    .ace_string {
        color: #a8e0a3;
    }

    .ace_comment {
        color: #808080;
    }

    .ace_function {
        color: #f0c674;
    }

    /* Darken the console (debug area) */
    #debugArea {
        background-color: #2d2d2d;
        color: #ffffff;
    }

    /* Darken reset button */
    .resetButton {
        background-color: #444444;
        color: #ffffff;
        border: 1px solid #555555;
    }

    /* Darken the game view pane (left side where animations appear) */
    .visualization,
    .display {
        background-color: #2b2b2b;
        color: #ffffff;
    }

    /* Footer and miscellaneous sections */
    .footer,
    .sub-footer {
        background-color: #222222;
        color: #cccccc;
    }

    /* Fixes for tables */
    table {
        background-color: #2b2b2b;
        color: #e0e0e0;
        border-collapse: collapse;
    }

    table th,
    table td {
        border: 1px solid #444444;
    }

    /* Darken progress bars */
    .progress-bar {
        background-color: #555555;
    }

    /* Darken side navigation */
    .sidebar {
        background-color: #333333;
        color: #ffffff;
    }

    .dropdown-menu {
        background-color: #2b2b2b;
        color: #ffffff;
    }

    /* Labels and badges */
    .badge,
    .label {
        background-color: #444444;
        color: #ffffff;
    }

    /* Darken tooltips */
    .tooltip {
        background-color: #333333;
        color: #ffffff;
    }
}
