style.css:
/* Universal Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    scroll-behavior: smooth;
}

body {
    font-family: 'Poppins', sans-serif;
    background: linear-gradient(135deg, #0f2027, #203a43, #2c5364);
    color: #f0f0f0;
    min-height: 100vh;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
}

/* Header Styling */
header {
    position: fixed;
    top: 0;
    width: 100%;
    background-color: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    padding: 20px 0;
    text-align: center;
    z-index: 1000;
    transition: all 0.5s ease;
}

header h1 {
    font-size: 48px;
    color: #fff;
    letter-spacing: 2px;
    text-shadow: 0 4px 6px rgba(0, 0, 0, 0.4);
}

/* Animated Gradient Border Button */
button {
    background: none;
    padding: 10px 30px;
    border: 2px solid transparent;
    border-radius: 50px;
    background-image: linear-gradient(#fff, #fff), radial-gradient(circle at top left, #ff7e5f, #feb47b, #ff6a5f);
    background-origin: border-box;
    background-clip: padding-box, border-box;
    color: #fff;
    font-size: 18px;
    cursor: pointer;
    position: relative;
    overflow: hidden;
    transition: all 0.5s ease;
}

button:hover {
    color: #000;
    background-image: linear-gradient(#000, #000), radial-gradient(circle at top left, #ff7e5f, #feb47b, #ff6a5f);
    box-shadow: 0px 15px 20px rgba(0, 0, 0, 0.4);
}

/* Section Styling */
section {
    width: 90%;
    max-width: 1200px;
    margin: 100px auto;
    padding: 20px;
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
    gap: 30px;
}

article {
    background: rgba(255, 255, 255, 0.15);
    padding: 20px;
    border-radius: 15px;
    transition: transform 0.3s ease-in-out;
    position: relative;
    overflow: hidden;
}

article:hover {
    transform: translateY(-10px) scale(1.05);
}

article::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, rgba(255, 126, 95, 0.6), rgba(254, 180, 123, 0.6));
    z-index: -1;
    transform: scaleX(0);
    transform-origin: bottom right;
    transition: transform 0.5s ease-in-out;
}

article:hover::before {
    transform: scaleX(1);
    transform-origin: top left;
}

/* Typography & Text Effects */
h2 {
    font-size: 24px;
    margin-bottom: 10px;
    color: #fff;
}

p {
    font-size: 16px;
    color: #ddd;
}

h2::after {
    content: '';
    display: block;
    width: 100px;
    height: 2px;
    background: linear-gradient(90deg, #ff7e5f, #feb47b);
    margin-top: 10px;
}

/* Footer with Neon Effect */
footer {
    background: #1f1f1f;
    padding: 30px;
    width: 100%;
    text-align: center;
    box-shadow: 0 -10px 20px rgba(0, 0, 0, 0.5);
}

footer p {
    font-size: 16px;
    color: #f0f0f0;
    text-transform: uppercase;
    letter-spacing: 1.5px;
    position: relative;
    text-shadow: 0 0 10px rgba(255, 126, 95, 0.75);
}

footer p::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 2px;
    background: linear-gradient(90deg, #ff7e5f, #feb47b);
    bottom: 0;
    left: 0;
    animation: neon-glow 3s infinite;
}

/* Neon Animation */
@keyframes neon-glow {
    0% {
        box-shadow: 0 0 5px rgba(255, 126, 95, 0.5), 0 0 20px rgba(255, 126, 95, 0.5), 0 0 30px rgba(255, 126, 95, 0.5);
    }
    50% {
        box-shadow: 0 0 10px rgba(255, 126, 95, 1), 0 0 30px rgba(255, 126, 95, 1), 0 0 50px rgba(255, 126, 95, 1);
    }
    100% {
        box-shadow: 0 0 5px rgba(255, 126, 95, 0.5), 0 0 20px rgba(255, 126, 95, 0.5), 0 0 30px rgba(255, 126, 95, 0.5);
    }
}
