style.css:
/* Light mode */
@media (prefers-color-scheme: light) {
    ::selection {
        background: rgba(200, 200, 200, 0.3); /* Light grey highlight */
        color: black; /* Keep text black */
    }
}

/* Dark mode */
@media (prefers-color-scheme: dark) {
    ::selection {
        background: rgba(80, 80, 80, 0.8); /* Dark grey highlight */
        color: white; /* White text */
    }
}
