style.css:
@-moz-document url("https://www.google.com/?igu=1") {
/* Set background GIF */
   body {
       background: url('https://i.imgur.com/2IinHiK.gif') no-repeat center center fixed !important;
       background-size: cover !important;
   }

   /* Hide original Google logo elements */
   #hplogo img, #hplogo svg, #hplogo div {
       display: none !important;
   }

   /* Insert custom logo using ::after pseudo-element */
   #hplogo::after {
       content: url("https://i.imgur.com/WsF7YIC.png");
       display: block;
       width: 600px !important;  /* Adjust this to change the width */
       height: auto !important;  /* Maintain aspect ratio */
       margin: 0 auto;           /* Center the logo */
   }

   /* Ensure the hplogo container itself allows the logo to scale */
   #hplogo {
       position: relative;
       width: 100% !important;   /* Ensure the logo container takes full width */
       max-width: none !important; /* Remove any max-width that might restrict the logo */
       z-index: 10 !important;
   }

   /* Hide footer, white bar, and other elements */
   #fbar, footer, #lb, .ctr-p, .uU7dJb, .c93Gbe {
       display: none !important;
   }
}
