style.css:
@-moz-document url-prefix("https://soundcloud.com/") {
html, body {
	background: #424242!important;
	color: #F5F5F5!important;
}

.sc-text {
    color: #eeeeee;
}

a.sc-link-dark {
  color: inherit!important;
}

a.sc-link-dark:hover {
  opacity: .72;
}

a.sc-link-light:hover {
    color: inherit!important;
    opacity: .72!important;
}

.g-dark input[type=password], .g-dark input[type=search], .g-dark input[type=text], .g-dark select, .g-dark textarea {
    color: #FFFFFF;
    background: #424242;
}

.g-dark input[type=search]:focus {
    background: #212121!important;
}

.sc-classic .headerMenu {
    background-color: #212121!important;
}

.sc-classic .headerMenu, 
.sc-classic .m-light .headerMenu__list, 
.sc-classic .mixedSelectionModule, 
.sc-border-light-right,
.sc-border-light-bottom,
.sc-button,
.sc-classic .playControls__bg, .sc-classic .playControls__inner {
    border-color:  #cccccc4a!important;
}

.headerMenu .headerMenu__link {
    color: inherit!important;
  	opacity: .72!important;
}

.artistShortcutTile__username span {
    color: #F5F5F5!important;
}

.sc-classic .artistShortcutTile__badge {
    border-color: #616161!important;
}

a.sc-ministats:hover {
    color: inherit!important;
}

.sc-ministats-small.sc-ministats-sounds:before {
    background-image: url(data:image/svg+xml;base64,);
    background-size: 16px 16px;
}


.sc-button {
  	background-color: #212121!important;
    color: #fff!important;
}

.sc-button-small.sc-button-follow:before {
    background-image: url(data:image/svg+xml;base64,);
}

.sc-classic .soundBadge__additional {
    background: #424242;
    background: linear-gradient(90deg, hsl(0deg 0% 25.88% / 10.2%), #424242 17px)!important;
}

.sc-button-small.sc-button-more:before {
    background-image: url(data:image/svg+xml;base64,);
    background-size: 14px 4px;
}

.sc-button-small.sc-button-like:before {
    background-image: url(data:image/svg+xml;base64,);
}

.sc-classic .tileGallery__sliderButton:after {
    border-color: #fff!important;
}

.sc-classic .tileGallery__sliderButton:active, .sc-classic .tileGallery__sliderButton:active:after, .sc-classic .tileGallery__sliderButton:focus, .sc-classic .tileGallery__sliderButton:focus:after, .sc-classic .tileGallery__sliderPeek:hover .tileGallery__sliderButton, .sc-classic .tileGallery__sliderPeek:hover .tileGallery__sliderButton:after {
    border-color: #f50!important;
}

.sc-classic .playControls__bg, .sc-classic .playControls__inner {
    background-color: transparent!important;
  	border-color: #757575!important;
}

.playControls__inner {
    background: linear-gradient(90deg, #a0a0a07a 50%, #ff550094) !important;
  	backdrop-filter: blur(3px);
}


.sc-classic .playbackSoundBadge__titleLink, .sc-classic .playbackSoundBadge__titleLink:visited {
    color: #eeeeee;
}

.sc-classic .playbackSoundBadge__titleLink:focus, .sc-classic .playbackSoundBadge__titleLink:hover {
    color: inherit!important;
    opacity: .72!important;
}

a.playbackSoundBadge__lightLink.sc-link-light.sc-link-secondary.sc-truncate.sc-text-h5 {
  color: #f5f5f5;
}

.sc-classic .volume__sliderWrapper {
    background: linear-gradient(180deg, #a0a0a07a 50%, #a0a0a07a) !important;
 		backdrop-filter: blur(3px);
  	border-color: #757575!important;
}

.sc-classic .volume__sliderWrapper:after {
    border-color: transparent transparent #a0a0a07a #a0a0a07a!important;
}

.sc-classic .playbackSoundBadge .playbackSoundBadge__follow, .sc-classic .playbackSoundBadge .playbackSoundBadge__like {
    background: transparent!important;
}

::-webkit-scrollbar-track
{
    background-color: #757575;
}
::-webkit-scrollbar
{
    width: 10px;
    background-color: #F5F5F5;
}

::-webkit-scrollbar-thumb
{
    background: linear-gradient(0deg, #ff550059 25%, #ff5500bd) !important;
    border-radius: 12px;
}
}
