style.css:
/* Alterar o fundo da página principal do Google */
body {
    background: linear-gradient(180deg, #0c0d18 0%, #1a1c2e 100%);
    color: #dcdfe4;
}

/* Estilo dos links */
a {
    color: #a8c8ff;
}

/* Alterar botões de busca */
input[type="submit"] {
    background-color: #0e0f1f;
    border: 1px solid #283352;
    color: #b0b8cc;
}

input[type="submit"]:hover {
    background-color: #1c223b;
    color: #ffffff;
}

/* Personalizar textos e títulos */
h1, h2, h3 {
    color: #c7d6ff;
}

/* Alterar caixa de pesquisa */
input[type="text"] {
    background-color: #101423;
    border: 1px solid #3a4b66;
    color: #b0b8cc;
}

/* Personalizar o footer */
footer {
    background-color: #0c0d18;
    color: #8f9bb3;
}
/* Barra de pesquisa com imagem de fundo transparente */
input[type="text"] {
    background: url(https://i.pinimg.com/564x/c3/bd/88/c3bd88f51c28bc56f593aa091dbf9dd9.jpg) no-repeat center;
    background-size: cover; /* Ajusta a imagem para cobrir a área */
    opacity: 0.8; /* Ajuste a opacidade conforme necessário */
    border: 1px solid #3a4b66; /* Mantém a borda */
    color: #b0b8cc; /* Cor do texto */
}
