style.css:
@-moz-document domain("bsky.app") {
/* Fundo da página */
  body {
    background-color: #ffe6f0 !important; /* Rosa claro */
    color: #ff66a3 !important; /* Rosa mais escuro para o texto */
  }

  /* Alterar a cor de fundo do cabeçalho */
  header {
    background-color: #ff99cc !important; /* Cabeçalho rosa médio */
    border-bottom: 2px solid #ff3385 !important; /* Rosa escuro */
  }

  /* Cor do texto do cabeçalho */
  header h1 {
    color: #ffffff !important; /* Texto branco no cabeçalho */
  }

  /* Fundo dos posts */
  article {
    background-color: #ffcce6 !important; /* Rosa suave para o fundo dos posts */
    border-radius: 10px !important;
    padding: 10px !important;
    margin-bottom: 15px !important;
  }

  /* Cor do texto dos posts */
  article p {
    color: #ff66a3 !important; /* Texto rosa escuro */
  }

  /* Links dentro dos posts */
  article a {
    color: #ff3385 !important; /* Rosa forte para links */
    text-decoration: underline !important;
  }

  /* Alterar cor dos botões de interação (curtir, compartilhar) */
  button {
    background-color: #ff99cc !important; /* Fundo rosa médio */
    color: #ffffff !important; /* Texto branco */
    border: 1px solid #ff66a3 !important; /* Borda rosa escuro */
  }

  button:hover {
    background-color: #ff66a3 !important; /* Botão escurece ao passar o mouse */
  }

  /* Campo de busca e inputs */
  input, textarea {
    background-color: #ffb3d9 !important; /* Fundo rosa para inputs */
    color: #ffffff !important; /* Texto branco */
    border: 1px solid #ff66a3 !important;
  }

  /* Rodapé */
  footer {
    background-color: #ff99cc !important; /* Fundo rosa médio para o rodapé */
    color: #ffffff !important;
    border-top: 2px solid #ff3385 !important;
  }
}
