style.css:
@-moz-document domain("kogama.com"), domain("kogama.com.br") {
/* Tło strony (stare tło z 2013 roku) */
    body#root-page-mobile {
        background: url('https://kogama.com.br/static/img/bg_1920_2.jpg') center top no-repeat fixed;
        background-color: #D4D9DB;
        color: #D4D9DB;
        box-shadow: 69 69 3400px rgba(0, 0, 0, 15.5); /* Delikatny wewnętrzny cień */
        font-family: 'Open Sans', sans-serif;
    }

    /* Logo KoGaMa w starym stylu */
    header .MuiTypography-h6 {
        background-image: url('https://web.archive.org/web/20130808222121im_/http://test.kogama.com/static/img/logo_bluewhite_4.png');
        background-size: contain;
        display: block;
        height: 80px;
        width: 250px;
        text-indent: -9999px;
    }

    /* Układ nawigacji w starym stylu */
    nav.MuiAppBar-root {
        background-color: #333333;
        padding: 10px 0;
    }

    nav.MuiAppBar-root a {
        color: #FFFFFF;
        text-transform: uppercase;
        font-weight: bold;
        padding: 15px;
        text-decoration: none;
        display: inline-block;
    }

    /* Styl przycisków w nawigacji */
    nav.MuiAppBar-root a:hover {
        background-color: #444444;
    }

    /* Przyciski "Play Now" */
    .play-button {
        background-color: #007ACC;
        color: #FFFFFF;
        padding: 10px;
        border-radius: 5px;
        font-weight: bold;
        text-transform: uppercase;
    }

    .play-button:hover {
        background-color: #005F8C;
    }

    /* Stylizacja miniatur gier (w starym stylu) */
    .thumbnails .item .box {
        background-color: #222222;
        border: 1px solid #444444;
        border-radius: 5px;
        margin-bottom: 20px;
        padding: 10px;
    }

    .thumbnails .item .box:hover {
        border-color: #007ACC;
    }

    /* Czcionki nagłówków */
    h1, h2, h3 {
        font-family: 'Open Sans', sans-serif;
        color: #FFFFFF;
        text-shadow: 1px 1px 0 rgba(0, 0, 0, 0.5);
    }

    /* Stylizacja przycisku logowania */
    .main-nav-login .login a {
        background-color: #009688;
        color: white;
        padding: 10px 20px;
        border-radius: 5px;
    }

    /* Stylizacja przycisku rejestracji */
    .main-nav-login .signup a {
        background-color: #4CAF50;
        color: white;
        padding: 10px 20px;
        border-radius: 5px;
        font-weight: bold;
    }

    /* Stopka */
    footer {
        background-color: #2D2D2D;
        color: #B0B0B0;
        padding: 20px 0;
    }

    footer a {
        color: #007ACC;
        text-decoration: none;
    }

    footer h4 {
        color: #FFFFFF;
        font-size: 16px;
    }

    /* Stylizacja linków w stopce */
    footer ul li a {
        color: #FFFFFF;
        font-weight: bold;
    }

    /* Promocje i wideo na stronie głównej */
    .promo h2 {
        font-family: 'Open Sans', sans-serif;
        color: #FFFFFF;
        font-size: 24px;
        margin-bottom: 15px;
    }

    .promo ul li {
        font-size: 18px;
        line-height: 1.5;
    }
}
