style.css:
@-moz-document domain("chatbotmaker.io") {
body {
        background-color: #181818 !important; /* Cor de fundo escuro */
        font-family: Arial, sans-serif !important; /* Fonte do texto */
        color: #e0e0e0 !important; /* Cor do texto claro */
        margin: 0;
        padding: 0;
    }

    /* Estilizando o cabeçalho */
    header {
        background-color: #1f1f1f !important; /* Cor do cabeçalho */
        color: #ffffff !important; /* Cor do texto do cabeçalho */
        padding: 15px;
        text-align: center; /* Alinhamento de texto */
    }

    /* Estilizando o container principal */
    .container {
        max-width: 1200px !important; /* Largura máxima */
        margin: 20px auto !important; /* Centralizar o container */
        padding: 20px;
        background-color: #292929 !important; /* Fundo do container */
        border-radius: 8px !important; /* Bordas arredondadas */
        box-shadow: 0 2px 5px rgba(0, 0, 0, 0.5) !important; /* Sombra mais forte */
    }

    /* Estilizando os botões */
    button {
        background-color: #007bff !important; /* Cor do botão */
        color: white !important; /* Cor do texto do botão */
        border: none !important;
        padding: 10px 20px !important; /* Preenchimento */
        border-radius: 5px !important; /* Bordas arredondadas */
        cursor: pointer !important; /* Muda o cursor para indicar que é clicável */
        transition: background-color 0.3s !important; /* Transição suave para pairar */
    }

    button:hover {
        background-color: #0056b3 !important; /* Cor do botão ao passar o mouse */
    }

    /* Estilizando links */
    a {
        color: #66b2ff !important; /* Cor dos links */
        text-decoration: none !important; /* Remover o sublinhado */
    }

    a:hover {
        text-decoration: underline !important; /* Sublinha ao passar o mouse */
    }

    /* Estilizando caixas de mensagem */
    .message-box {
        background-color: #3a3a3a !important; /* Cor da caixa de mensagem */
        border-radius: 5px !important; /* Bordas arredondadas */
        padding: 10px !important;
        margin: 10px 0 !important; /* Espaço entre as mensagens */
    }

    /* Estilizando o rodapé */
    footer {
        background-color: #1f1f1f !important; /* Cor do rodapé */
        color: #ffffff !important; /* Cor do texto do rodapé */
        text-align: center !important; /* Alinhamento de texto */
        padding: 10px !important;
        position: relative !important;
        bottom: 0 !important;
        width: 100% !important; /* Largura total */
    }

    /* Scrollbar */
    ::-webkit-scrollbar {
        width: 10px !important;
    }

    ::-webkit-scrollbar-track {
        background: #1e1e1e !important; 
    }

    ::-webkit-scrollbar-thumb {
        background: #333 !important; 
    }

    ::-webkit-scrollbar-thumb:hover {
        background: #555 !important; 
    }
}
