style.css:
/* Dark Academia Theme for Stylish */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://"), url-prefix("https://") {
/* General background and text color */
    body {
        background-color: #2C2A2A; /* Deep dark brown */
        color: #D4C7A3; /* Muted beige */
        font-family: "Garamond", "Times New Roman", serif; /* Classic font */
        line-height: 1.6;
    }

    /* Headings styling */
    h1, h2, h3, h4, h5, h6 {
        color: #EAE4D3; /* Off-white for headers */
        font-family: "Georgia", serif; /* Another classic serif */
        font-weight: bold;
    }

    /* Links */
    a {
        color: #8C7B6E; /* Faded brown for links */
        text-decoration: none;
        border-bottom: 1px solid #8C7B6E;
    }

    a:hover {
        color: #B7A392; /* Slightly lighter on hover */
        border-bottom-color: #B7A392;
    }

    /* Button styling */
    button, input[type="submit"], input[type="button"] {
        background-color: #4A4943; /* Dark gray-brown */
        color: #D4C7A3;
        border: 1px solid #8C7B6E;
        padding: 8px 16px;
        font-family: "Garamond", serif;
        border-radius: 4px;
    }

    button:hover, input[type="submit"]:hover, input[type="button"]:hover {
        background-color: #5C5A54;
        border-color: #B7A392;
    }

    /* Blockquote styling */
    blockquote {
        color: #B7A392;
        border-left: 4px solid #8C7B6E;
        padding-left: 16px;
        font-style: italic;
        background-color: #3B3A35;
    }

    /* Table styling */
    table {
        border-collapse: collapse;
        width: 100%;
        background-color: #3C3B37;
    }

    th, td {
        border: 1px solid #8C7B6E;
        padding: 12px;
        color: #D4C7A3;
    }

    th {
        background-color: #4A4943;
    }

    /* Inputs, text areas */
    input[type="text"], textarea, select {
        background-color: #3B3A35;
        color: #D4C7A3;
        border: 1px solid #8C7B6E;
        padding: 10px;
        font-family: "Garamond", serif;
    }

    /* Scrollbar styling */
    ::-webkit-scrollbar {
        width: 12px;
    }

    ::-webkit-scrollbar-track {
        background: #2C2A2A; /* Scrollbar track color */
    }

    ::-webkit-scrollbar-thumb {
        background: #8C7B6E; /* Scrollbar thumb color */
        border-radius: 10px;
    }

    ::-webkit-scrollbar-thumb:hover {
        background: #B7A392;
    }

    /* Code block styling */
    pre, code {
        background-color: #3B3A35;
        color: #D4C7A3;
        border: 1px solid #8C7B6E;
        padding: 8px;
        font-family: "Courier New", monospace;
    }

    /* Adjust specific site elements like headers, navbars */
    header, nav, footer {
        background-color: #2C2A2A; /* Keep header/footer dark */
        color: #D4C7A3;
        border-bottom: 1px solid #8C7B6E;
    }

    /* Images */
    img {
        filter: brightness(80%);
        border: 1px solid #8C7B6E;
        border-radius: 5px;
    }
}
