style.css:
/* Definindo cores principais */
:root {
    -cor-principal: #C71585; /* Rosa escuro */
    -cor-secundaria: #FF69B4; /* Rosa quente */
    -cor-fundo: #F8F0F8; /* Fundo claro */
    -cor-texto: #FFFFFF; /* Texto branco */
}

/* Estilo do corpo */
body {
    background-color: var(--cor-fundo);
    color: var(--cor-texto);
    font-family: 'Arial', sans-serif;
    margin: 0;
    padding: 20px;
}

/* Cabeçalho */
header {
    background-color: var(--cor-principal);
    padding: 20px;
    text-align: center;
    border-radius: 10px;
}

/* Títulos */
h1, h2, h3 {
    color: var(--cor-secundaria);
}

/* Botões */
button {
    background-color: var(--cor-secundaria);
    color: var(--cor-texto);
    border: none;
    border-radius: 5px;
    padding: 10px 20px;
    cursor: pointer;
    transition: background-color 0.3s;
}

button:hover {
    background-color: var(--cor-principal);
}

/* Links */
a {
    color: var(--cor-secundaria);
    text-decoration: none;
}

a:hover {
    text-decoration: underline;
}

/* Rodapé */
footer {
    background-color: var(--cor-principal);
    color: var(--cor-texto);
    text-align: center;
    padding: 10px;
    position: relative;
    bottom: 0;
    width: 100%;
}
