style.css:
@-moz-document domain("app.adalo.com") {
.app {
        color: var(--extradark);
    }
    .navbar-user-avatar .icon {
        background-color: var(--extradark);
    }
    .icon {
        background-color: var(--extradark);
    }
    .canvas {
        background-color: var(--extralight);
    }
    .page-shadow {
        box-shadow: none !important;
        border: 1px solid var(--medium);
        background-color: transparent !important;
    }
    .page-title-text {
        color: var(--dark);
    }
    .data-collections-section-title {
        color: var(--dark);
    }
    .app-navbar {
        box-shadow: none;
        background: none;
    }
    .panel {
        border-radius: var(--radius) !important;
    }
    .group-child-component {
        border: var(--border);
        background-color: var(--white)
    }
    .editor-left-nav {
        border-right: none !important;
        background: none !important;
    }
    .edit-bar .panel-body {
        height: auto !important;
    }
    .data-accordion-item .accordion {
        background-color: var(--white);
        border: 1px solid var(--light);
    }
    .layer-info-sub {
        background-color: var(--white) !important;
    }
    .edit-bar-empty {
        background: none !important;
        box-shadow: none !important;
    }
    .layer-item .layer-info .layer-info-sub .layer-label .layer-label-subtitle {
        color: var(--dark);
    }
    .link-wrapper .link-path {
        stroke: var(--medium) !important;
    }
    .canvas-link-map-selected .link-wrapper:not(:hover) .link-arrow {
        fill: var(--extradark);
    }
    .page-title-text {
        background: none !important;
    }
    .editor-left-nav-item-sub {
        box-shadow: var(--shadow);
        background: #fff !important;
    }
    .app-navbar-stripe {
        display: none!important;
    }
    .editor-preview-button {
        background-color: #fff !important;
        border-radius: var(--radius);
        box-shadow: var(--shadow);
    }
    .editor-share-button {
        background-color: #fff !important;
        border-radius: var(--radius);
        box-shadow: var(--shadow);
    }
    .app-switcher {
        background-color: #fff !important;
        border-radius: var(--radius);
        box-shadow: var(--shadow);
    }
    .app-switcher .navbar-dropdown-children {
        border-radius: var(--radius);
    }
    .navbar-dropdown-title {
        border-radius: var(--radius) !important;
    }
    .layer-info-sub {
        border: var(--border);
    }
    .navbar-user-info {
        background-color: var(--white);
        box-shadow: var(--shadow);
        padding-top: 0px;
        padding-right: 9px;
        padding-bottom: 0px;
        padding-left: 9px;
        border-radius: 90px;
        height: 40px;
        margin-top: 10px;
        margin-left: 30px;
    }
    .edit-bar-empty {
        display: none !important;
    }
    span.page-title-text {
        font-weight: 500;
        color: var(--dark);
        background-color: var(--light) !important;
        font-size: 12px;
        padding-left: 6px;
        padding-right: 6px;
        padding-top: 6px;
        padding-bottom: 6px;
        border-radius: 3px;
    }

    ._pendo-badge {
        display: none !important;
    }
}
