style.css:
/* Tema oscuro genérico para Stylish */
/* Aplica esto a los sitios web que desees oscurecer */

/* Selector general para afectar a toda la página */
html, body, body * {
  background-color: #1a1a1a !important;
  color: #e0e0e0 !important;
  border-color: #4a4a4a !important;
}

/* Estilos para enlaces */
a, a:visited {
  color: #3391ff !important;
}

a:hover {
  color: #5aaaff !important;
}

/* Estilos para entradas de texto y áreas de texto */
input, textarea, select {
  background-color: #2c2c2c !important;
  color: #ffffff !important;
  border: 1px solid #4a4a4a !important;
}

/* Estilos para botones */
button, input[type="submit"], input[type="button"] {
  background-color: #4a4a4a !important;
  color: #ffffff !important;
  border: none !important;
}

button:hover, input[type="submit"]:hover, input[type="button"]:hover {
  background-color: #5a5a5a !important;
}

/* Estilos para encabezados */
h1, h2, h3, h4, h5, h6 {
  color: #ffffff !important;
}

/* Ajuste para imágenes y medios para evitar que se vean demasiado brillantes */
img, video, iframe {
  filter: brightness(0.8) contrast(1.2);
}
