style.css:
@-moz-document domain("spicychat.ai") {
/* Fondo principal con efecto borroso */
    body {
        background: rgba(25, 25, 25, 0.8);
        backdrop-filter: blur(10px);
        margin: 0;
        font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen, sans-serif;
        color: #e0e0e0;
    }

    /* Encabezados con tono sutil */
    h1, h2, h3, h4 {
        color: #ffffff;
        text-shadow: 0px 2px 4px rgba(0, 0, 0, 0.3);
    }

    /* Enlaces y texto resaltado en un tono suave */
    a, .highlight {
        color: #b3b3b3;
        text-decoration: none;
        transition: color 0.3s ease;
    }
    a:hover {
        color: #ffffff;
    }

    /* Contenedores con borde suave y fondo borroso */
    .chat-container, .message-container {
        background: rgba(40, 40, 40, 0.6);
        backdrop-filter: blur(8px);
        border-radius: 12px;
        padding: 15px;
        margin: 10px 0;
        box-shadow: 0px 2px 10px rgba(0, 0, 0, 0.5);
    }

    /* Botones elegantes */
    button, .btn {
        background-color: #3a3a3a;
        color: #ffffff;
        border: none;
        border-radius: 10px;
        padding: 12px 20px;
        cursor: pointer;
        transition: background-color 0.3s ease;
    }
    button:hover, .btn:hover {
        background-color: #505050;
    }

    /* Ajuste del input de mensaje */
    input[type="text"] {
        background-color: rgba(50, 50, 50, 0.7);
        border: 1px solid #666;
        border-radius: 8px;
        padding: 10px;
        width: 100%;
        color: #e0e0e0;
    }
    input[type="text"]::placeholder {
        color: #a0a0a0;
    }

    /* Ajuste de las im√°genes de perfil */
    .avatar {
        border-radius: 50%;
        box-shadow: 0px 2px 8px rgba(0, 0, 0, 0.3);
    }
}
