style.css:
@-moz-document domain("youtube.com") {
/* Background */
    body {
        background-color: #ffeef8 !important;
        color: #ff69b4 !important;
    }

    /* Header */
    #header, #masthead, #container {
        background-color: #ffb3d9 !important;
    }

    /* Custom YouTube Logo */
    #logo {
        background-image: url('https://upload.wikimedia.org/wikipedia/commons/thumb/2/24/YouTube_icon_%282013-2017%29.png/1200px-YouTube_icon_%282013-2017%29.png') !important;
        background-size: cover !important;
        width: 100px; /* Adjust as needed */
        height: 40px; /* Adjust as needed */
    }

    /* Video Titles */
    h1.title, h2.title {
        color: #ff1493 !important;
    }

    /* Buttons */
    ytd-button-renderer {
        background-color: #ff69b4 !important;
        border-radius: 10px;
    }

    ytd-button-renderer:hover {
        background-color: #ff85c2 !important;
    }

    /* Sidebar */
    #sections, #related {
        background-color: #ffccf2 !important;
    }

    /* Thumbnails */
    ytd-thumbnail {
        border-radius: 10px;
    }

    /* Footer */
    #footer {
        background-color: #ffb3d9 !important;
        color: #ff69b4 !important;
    }

    /* Links */
    a {
        color: #ff1493 !important;
    }

    a:hover {
        color: #ff69b4 !important;
    }

    /* Hearts Background */
    #masthead::after {
        content: "♥ ♥ ♥ ♥ ♥ ♥ ♥ ♥";
        display: block;
        font-size: 30px;
        color: #ff69b4;
        text-align: center;
        margin: 10px 0;
        animation: heartbeat 1s infinite;
    }

    @keyframes heartbeat {
        0%, 100% {
            transform: scale(1);
        }
        50% {
            transform: scale(1.2);
        }
    }
}
