style.css:
/* Basic Dark Theme */
body {
  background-color: #181818; /* Dark background */
  color: #e8e8e8; /* Light text */
}

/* Links */
a {
  color: #0080ff; /* Light blue links */
}

a:visited {
  color: #800080; /* Purple visited links */
}

/* Headers */
h1, h2, h3, h4, h5, h6 {
  color: #fff; /* White headers */
}

/* Code Blocks */
code,
pre {
  background-color: #282828; /* Slightly lighter background for code */
}

/* Input Fields and Textareas */
input,
textarea {
  background-color: #282828;
  color: #e8e8e8;
  border: 1px solid #505050;
}

/* Buttons */
button {
  background-color: #383838;
  color: #e8e8e8;
  border: 1px solid #505050;
}

/* Tables */
table {
  border-collapse: collapse;
  width: 100%;
}

th, td {
  border: 1px solid #505050;
  padding: 8px;
  text-align: left;
}

/* Images */
img {
  filter: brightness(0.8); /* Slightly dim images for better contrast */
}
