style.css:
/* Dark mode background */
body {
    background-color: #121212; /* Dark gray/black background */
    color: #e0e0e0; /* Light gray text for readability */
}

/* Style for links in dark mode */
a {
    color: #bb86fc; /* Light purple for links */
}

a:hover {
    color: #3700b3; /* Darker purple for hover effect */
}

/* Additional styling for buttons */
button {
    background-color: #333333; /* Dark background for buttons */
    color: #ffffff; /* White text */
    border: 1px solid #444444; /* Subtle border */
}

button:hover {
    background-color: #444444; /* Slightly lighter on hover */
}

/* Adjusting input fields */
input, textarea {
    background-color: #1c1c1c; /* Dark background for inputs */
    color: #ffffff; /* White text */
    border: 1px solid #333333; /* Border for inputs */
}

input::placeholder, textarea::placeholder {
    color: #888888; /* Lighter color for placeholders */
}

/* Dark mode for navigation bar */
nav {
    background-color: #1a1a1a; /* Dark background for nav */
    color: #ffffff; /* White text */
}

/* Footer in dark mode */
footer {
    background-color: #1a1a1a; /* Dark background for footer */
    color: #cccccc; /* Light gray text */
}
