style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.qype.com/mypage") {
/*li.StreamSlot (div.StreamItem, ul.StreamReplies( li.StreamReplyBox ( div.Review ),..))*/

.StreamSlot {
margin-bottom: 5px !important;
}

/*Stream-Item -> Platzinfo*/
div.StreamItem {
height:25px !important;
overflow: hidden !important;

/*padding-top: 4px !important;*/
}

div.StreamItem:hover {
height:100% !important;
overflow: auto !important;
}

/*StreamReplayBox -> Beitrag, Meldung ...*/
li.StreamReplyBox {
height:20px !important;
overflow: hidden !important;

margin-bottom: 1px !important;
padding-top: 1px !important;

}

li.StreamReplyBox:hover {
height:100% !important;
overflow: auto !important;
}


}
