style.css:
/* Simple Right to Left Twitter
Written by: "MR.WILL"
Last Update: May 1, 2010
contact: ali.mousavi@gmail.com
*/



@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {


/*----------------HOME-----------------*/

#latest_text {
direction: rtl;
font-family: Tahoma;
text-align: right;
float: right;
}

.common-form textarea {
direction: rtl;
font-family: Tahoma !important;
text-align: right;
}

#tweeting_controls {
float: left !important;
padding: 5px 5px 0 3px !important
}

#update_notifications {
margin: 3px 10px 0 8px !important;
padding: 2px 4px 2px 4px !important;
float: right !important;
width: 370px !important;
}

span.thumb {
right: 0 !important;
margin: 0 0 0 5px !important;
}

ol.statuses {
direction: rtl;
width: 520px;
}

ol.statuses span.status-body {
margin-right: 65px !important;
direction: rtl;
text-align: right;
width: 450px !important;
font-family: Tahoma;
font-size: 13px;
}

#content li .meta {
direction: ltr !important;
text-align: left !important;
}

.actions-hover {
right: 55px !important;
}

/*---------------USER---------------*/

body#profile ol.statuses span.status-body {
margin-right: 5px !important;
width: 515px !important;
}

body#profile ol.statuses span.status-body span.status-content {
padding-left: 35px !important;
}

body#profile ol.statuses .entry-meta {
margin-left: 5px !important;
}

body#profile ol.statuses span.actions {
direction: ltr !important;
left: 5px !important;
}


body#profile ol.statuses .actions-hover {
right: 0 !important;
}

/*---------------USER SIDEBAR---------------*/
body#profile ul.about span.fn, body#profile ul.about span.adr, body#profile ul.about span.bio {
font-family: Tahoma;
}



/*---------------DIRECT MESSAGE---------------*/

body#create .status-btn {
float: left !important;
}

body#create .bar {
width: 515px;
}

body#create .bar a{
font-family: Tahoma;
}

}
