style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dzone.com") {
#left 
{
	bottom:0pt;
	left:0pt;
	overflow:hidden;
	position:absolute;
	top:31px;
	width:314px;
	z-index:6;
	visibility: hidden !important; 
}
#content 
{
	bottom:0pt;
	left:1px !important;
	outline-color:invert;
	outline-style:none;
	outline-width:medium;
	overflow:auto;
	position:absolute;
	right:0pt;
	top:124px;
	z-index:3;
}
#pageTitle 
{
	color:#333333;
	margin:10px 0pt 0pt 0px !important;
	z-index:5;
}
#head 
{
	background:#EBF2FD none repeat scroll 0%;
	color:#AAAAAA;
	display:block;
	font-weight:bold;
	height:25px;
	left:0pt;
	margin:0pt 0pt 0pt 0px !important;
	overflow:hidden;
	padding-top:5px;
	position:relative;
	top:0pt;
	z-index:5;
}
}
