style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("blog.foreignpolicy.com")
{
	#wrapper, #theatre, #content-lz
	{
		width: 100% !important
	}
	
	.blog_entry
	{
		margin-left: 1.5% !important;
		width: 98% !important;
	}
	
	#content-lz
	{
		padding: 0px !important;
	}
	
	DIV[style="margin: 20px 0pt;"][class="content"]
	{
		margin: 0px 0pt !important;
	}
	
	P
	{
		line-height: normal !important;
	}
	
	.blog_entry
	{
		background-image: none !important;
		margin-bottom: 0px !important;
		padding-bottom: 0px !important
	}
	
	#flag, #header, #featured-blog, #banner-blog, #aux-rail, .agg_post_meta, .subjects, .smallgray, .blog-hed, HR
	{
		display: none !important
	}
	
	H2
	{
		display: none !important
	}
	
	#mb-body .blurb
	{
		font-family: Arial,Helvetica,sans-serif !important;
		font-size: 1em !important;
		line-height: normal !important;
	}
}
