* {
	font-family: Verdana, Geneva, sans-serif !important;
	font-size: 8pt !important;
	font-weight:normal !important;
}

body {
	background: #0b0a09 !important;
	color: #5F5645 !important;
	margin: 0px !important;
	padding: 0px !important;
}

html {
	margin: 0px !important;
	padding: 0px !important;
}

a {
	color: #393430 !important;
}

a:hover {
	color: #042700 !important;
}

hr, #filter-button, #option-button, #ft,  #hd, #footer center, #navbotr, #navtopr, img[src*="/dontblockthis"], .logo, #ad, a[href*="http://www.4chan.org/advertise/"], .rules, iframe[src="http://www.4chan.org/rotating.php"], img[src*="http://content.4chan.org/tmp"],
td[align*="right"], .doubledash, iframe {
	display:none !important;
}


#navtop, #navbot {
	width: 100% !important;
	padding: 0px !important;
	margin: 0px !important;
	text-align: center !important;
	color: #2D211A !important;
	top: 0 !important;
	z-index: 30;
}

form {
	margin: 1em !important;
}

.inputtext, textarea {
	width: 295px !important;
}

.postertrip {
	color: #768657 !important;
}

.postblock {
	background: #0b0a09 !important;
	border: 1px solid #40312b !important;
	color: #393430 !important;
}

.reply {
	background-color:#171006 !important;
	padding: .2em !important;
	border: 1px solid #221A17 !important;
	color: #6D634B !important;
	-moz-border-radius: 4px 4px 4px 4px !important;
}

.replyhl {
	background:#171412 !important;
	padding: .2em !important;
	border: 1px solid #2C2722  !important;
	color: #8B897A !important;
	-moz-border-radius: 4px 4px 4px 4px !important;
}

.postername {
	color: #3b7f8e !important;
}


.commentpostername, .omittedposts, .abbr {
	color: #5E5037 !important;
}

.filetitle, .replytitle {
	color: #b9633e !important;
	font-size: inherit;
}

th {
	background: #0b0d07 !important;
	padding: .2em !important;
	font-size: small;
	width: 200px !important;
}


.boxbar {
	background: #0b0a09 !important;
}

.box-outer, .box-inner {
	border: none !important;
	background: #0b0a09 !important;
}

.pages {
	background: #0b0a09 !important;
	border: none !important;
	-moz-border-radius: 3px !important;
}

#recent-images li{
	background: #0b0a09 !important;
}

pre, textarea {
	background: #0b0a09 !important;
	font-size: inherit ! important;
	border: 1px solid gray !important;
}

INPUT, input {
	color: white !important;
	background: #0b0a09 !important;
}
a[href*="mailto:sage"]:after,
a[href*="mailto:SAGE"]:after {
	content: " (sage)" !important;
}


div.postblock {
text-align: center !important;
background: #0b0a09 !important;
border-bottom: 1px solid #909090 !important;
padding: 2px !important;
font: 12px Calibri !important;
font-weight: lighter !important;
color: #909090 !important;
-moz-border-radius: 5px 5px 0px 0px !important;
}

SPAN[class="4chan_win_header_buttons"] {
top: 2px !important;
}

[id^="4chan_ext"][class="reply"], .reflinkpreview {
background: #0b0a09 !important;
border: 1px solid #909090 !important;
-moz-border-radius: 5px !important;
}

postarea {
padding: 0px !important;
margin: 0px !important;
width: 100% !important;
}

.postarea textarea, .postarea .inputtext, .postarea input:not([type="button"]):not([type="checkbox"]):not([type="submit"]):not([type="reset"]):not([name="sub"]) {
color: #909090 !important;
background: #0b0a09 !important;
border: 1px solid #909090 !important;
-moz-appearance: none !important;
font: 12px Calibri !important;
padding: 0px !important;
width: 300px !important;
}

.postarea select[name="filetag"] {
color: #909090 !important;
background: #0b0a09 !important;
border: 1px solid #909090 !important;
-moz-appearance: none !important;
font: 12px Calibri !important;
padding: 0px !important;
width: 300px !important;
}

.postarea select[name="from"] {
color: #909090 !important;
background: #0b0a09 !important;
border: 1px solid #909090 !important;
-moz-appearance: none !important;
font: 12px Calibri !important;
padding: 0px !important;
width: 302px !important;
}

td > .inputtext {
height: inherit !important;
padding: 1px !important;
}

.inputtext:focus {
border: 1px solid #5a5a5a !important;
}

input.inputtext {
height: inherit !important;
}

.postarea td.postblock {
display: none !important;
}

td.postblock {
background: #0b0a09 !important;
border: 1px solid #909090 !important;
color: #909090 !important;
font: 10px Calibri !important;
text-align: center !important;
padding: 0px 5px !important;}

.postarea table, .postarea span, .postarea span a {
color: #909090 !important;
font: 12px Calibri !important;
padding: 0px !important;
margin: 0px !important;
}

.postarea td.rules {
display: none !important;
}

.postarea small {
display: none !important;
}

.postarea input[value="Submit"], .postarea input[type="button"]:not([value="Report"]), .postarea input[value="Go"] {
border: 1px solid #909090 !important;
background: #0b0a09 !important;
color: #909090 !important;
font : 10px Calibri !important;
text-transform: uppercase !important;
text-align: center !important;
padding: 0px !important;
-moz-appearance: none !important;
height: 16px !important;
width: 50px !important;
margin: -1px 0px 0px 1px !important;
}

.postarea input[name="sub"] {
width: 249px !important;
}

.postarea input[value="Go"] {
margin-top: -1px !important;
height: 18px !important;
}

.postarea input[value="Submit"] {
display: inline-block !important;
margin-left: -1px !important;
position: relative !important;
top: 0px !important;
padding: 0px !important;}

.postarea input[type=file] {
margin: -1px !important;
}

.postarea input:not([type="button"]):not([type="checkbox"]):not([type="submit"]):not([type="reset"]):not([name="sub"]):not([name="name"]):not([name="email"]):not([name="com"]):not([name="pwd"]) {
width: 249px !important;
}


.hidethread, .unhidethread, .watchthread, .expandthread, .quickreply, .hidewatchedthreads, .refreshwatchedthreads, .restorewatchedthreads, * html 
.hidethread, .unhidethread, .watchthread, .expandthread, .quickreply, .hidewatchedthreads, .refreshwatchedthreads, .restorewatchedthreadsimg, img[src^="chrome://4chan/skin/buttons/"], img[alt="Locked"], img[alt="closed"], img[alt="sticky"] {
background-image: url('http://img193.imageshack.us/img193/5427/extbuttons.png') !important; }
