style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("inthemix.com.au") {

#site {
min-width: 990px !important;
}
body#forum-display #site, body#show-thread #site {
width:auto !important;
margin: 0 100px !important;
}

body#forum-display .grouping {
width:73%!important;
}
#forums-listing .forum .forum-title h3 a {
background-color:#FF981F !important;
}
#forums-listing .forum .forum-title h3 a:hover {
background-color:#333333 !important;

}
#breakout-key #key-contains-new p {
background-color:#FF981F!important;
}
#thread-title h3 {
color:#666666 !important;
}
.post .post-user-name a {
color:#333333 !important;
}
.post .post-message-content {
padding:10px 20px 10px 15px !important;
}
#threadslist .thread-details h3 a {
color:#666666 !important;
}
#threadslist .thread-details h3 a:hover {
color:#FE4938 !important;
}
#threadslist .thread-details h3.thread-new-post {
color:#000000 !important;
}


}
