style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("jensweinreich.de") {

    #contentleft p > img, #contentleft p > a > img, #contentleft div.wp-caption img {
        border: 0 none !important;
        padding-bottom: 9px !important;

        -moz-box-shadow: 2px 2px 6px #666 !important;
        -webkit-box-shadow: 2px 2px 6px #666 !important;
        box-shadow: 2px 2px 6px #666 !important;
    }

    #contentleft a[href^="http://flattr.com"] img[src^="http://api.flattr.com"] {
        -moz-box-shadow: none !important;
        -webkit-box-shadow: none !important;
        box-shadow: none !important;
    }

    #contentleft .postinfo img {
        background: none transparent !important;
    }

}
