style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("groza.ru") {
    td, th, p, li { font-size: 15px !important; }
    #container #collapseobj_wrapper #collapseobj_toolbar { height: 70px !important; }
    #container #collapseobj_wrapper #collapseobj_toolbar > table { width: 1100px !important }
    #container #collapseobj_wrapper #collapseobj_toolbar > table > tbody > tr > td > ul > li > img { margin-top: 10px; }
    #container #collapseobj_wrapper #breadcrumb { height: 4em !important }
    input, textarea, select, select option { font-size: 15px !important; }
}
