style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
url-prefix("http://www.tumblr.com/dashboard"),
url-prefix("http://www.tumblr.com/blog"),
url-prefix("http://www.tumblr.com/drafts"),
url-prefix("http://www.tumblr.com/queue"),
url-prefix("http://www.tumblr.com/like"),
url-prefix("http://www.tumblr.com/tagged/"),
url-prefix("https://www.tumblr.com/dashboard"),
url-prefix("https://www.tumblr.com/blog"),
url-prefix("https://www.tumblr.com/drafts"),
url-prefix("https://www.tumblr.com/queue"),
url-prefix("https://www.tumblr.com/like"),
url-prefix("https://www.tumblr.com/tagged/")
{
/*[[reblog-emphasis]]*/

* { border-radius: 0px !important; }


/*#########
# dashboard #
#########*/
body {
font-family: 'MS UI Gothic', Arial, sans-serif !important;
line-height: 130% !important;
}
#container {
width: auto !important;
padding: 0 !important;
}
div.l-header {
width: 100% !important;
height: auto !important;
position: relative !important;
}

div.l-header #logo {
display: none !important;
}

#tabs_outter_container {
left: auto !important;
top: auto !important;
width: 100% !important;
height: 40px !important;
}
#user_tools {
position: absolute !important;
float: right !important;
padding: 0 !important;
text-align: right !important;
right: 0 !important;
}
#tabs_container {
position: absolute !important;
float: left !important;
padding: 8px 0px !important;
}
li.tab {
padding: 0 7px  !important;
min-height: 18px !important;
}
li.tab a {
line-height: 100% !important;
}

#footer {
position: fixed !important;
bottom: 0px !important;
width: 100% !important;
padding: 5px !important;
margin: 0 !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
padding: 50px 150px 0px 0px !important;
}
#right_column {
width: 150px !important;
min-height: 600px !important;
margin-right: -150px !important;
overflow: hidden !important;
}
#left_column {
width: 100% !important;
}







.gradient {
display: none !important;
}
#search_controls {
width: 130px !important;
}
ul.right_column_section li a {
padding: 8px 13px 10px 34px !important;;
}
ul.right_column_section .hide_overflow {
font-size: 10px !important;
}

.radar img {
max-width: 150px !important;
height: auto !important;
}



#new_post_buttons {
margin: 0px 0px 8px 0px !important;
padding: 0px !important;
}
#new_post {
margin: 0px !important;
padding: 0px !important;
}
#posts .post.new_post {
min-height: 64px !important;
}
.new_post_label {
font-size: 0px !important;
color: transparent !important;
width: 48px !important;
height: 48px !important;
padding: 0 !important;
}
.new_post_label i {
font-size: 32px !important;
position: relative !important;
top: auto !important;
transition-delay: 0s;
transition-duration: 0.08s;
transition-property: line-height;
transition-timing-function: linear;
}

#posts.posts > .post_container {
margin-bottom: 4px !important;
}
.post {
padding: 15px !important;
margin-top: 0px !important;
}
.post_full{
width: 100% !important;
}

#post_content {
margin-top: 20px !important;
background-color: rgba(255,255,255, 1.0) !important;
}

.post_content div {
display: block !important;
}
div.post_content img {
width: auto !important;
}
div.post_content img.enlarged {
max-width:100% !important;
max-height:100% !important;
width: auto !important;
height: auto !important;
}

embed {
max-width: 100% !important;
height: auto !important;
}
.video,
.video_embed {
max-width: 100% !important;
}
#posts .same_user_as_last {
margin-top: 0 !important;
}

.caption blockquote {
float: left !important;
border-left-width: 0px !important;
padding-left: 0px !important;
margin: 0 !important;
}
.caption p {
padding: 0px 3px 0px 0px !important;
margin: 0 !important;
}
.notification {
width: auto !important;
}

.flipcard {
margin-top: 20px !important;
max-width:100% !important;

}
.flipcard_back table {
width: 200px !important;
float: right !important;
}







}



/*#########
# settings #
#########*/
@-moz-document 
regexp("^https?://www.tumblr.com/blog/.*/settings")
{
div#main_column {
padding-left: 0px !important;
padding-right: 0px !important;
min-width: 440px !important;
width: 70% !important;
}
div#page_nav {
float: left !important;
min-width: 120px !important;
width: 20% !important;
}
.vertical_tab_set {
width: auto !important;
}
div.settings_container > div.left_column {
width: auto !important;
}
div#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
width: auto !important;
padding-right: 0px !important;
}

}
