style.css:
/*
name:       dA Search Bar Hide (for v7)
version:    0.1
author:     tealpeach.deviantart.com
created:    july 10, 2010 - v0.1
updates:    none yet
short info: hides the small search bar in the deviantART header. can also move the search bar to the right.
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {

#overhead-sc > DIV:first-child + DIV 
{ display: none !important;
/*position:absolute !important;*/
/*right:160px !important;*/
/*Unblock "position:absolute" and "right:160px," then block "display: none" to move the search bar right.*/ }

#oh-menu-deviant 
{ padding-left: 3px !important; }

}
