style.css:
@-moz-document domain("mixcloud.com") {
/* Force playlist to be taller and add scrollbar */
    [class*="styles__UpNextArea"] {
        height: 65px !important;
        overflow-x: scroll !important;
    }

    /* Adjust height of the player wrapper's backing block to extend with scrollbar height (doesn't need to be precise) */
    [class*="styles__PlayerWrapper"]::before {
        height: 125px;
    }

    /* Make the linear play progress bar thicker. */
    [class*="AudioCardProgress__ProgressContainer"] {
        height: 3px;
        background: linear-gradient(to bottom, #eee 50%, #ccc 80%);
    }
    
    /* Make the linear play progress bar on items show up darker for proper contrast. */
    [class*="AudioCardProgress__ProgressBar"] {
        background: #333;
    }

    /* Different color for the currently playing track. */
    .cxYzQU {
        background: #c10;
    }

    /* No bold text on 'add to list' menus */
    .popover-list-item * {
        font-weight: normal !important;
    }
}
