style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.nytimes.com") {

#shell{
width: 100% !important;
position: absolute;
top: 0%;
}

#TP_fixed_toolbar, #upNextWrapper, .articleTools, .relatedSearchesModule, #footer, #insideNYTimes, #memberTools,.cColumn,.tabsContainer, #searchWidget, #relatedArticles, .subNavigation,.articleSpanImage {display:none !important}
#abColumn {
width : 100% !important; 
align:center !important;
}
.articleBody{
align:center !important ;
width:90% !important;
font-size:110% !important;
}
#article p
{line-height:1.7em;
 margin-bottom:2.5em;
 font-family: "Lucida Grande";
}
 a[title^="More "][href^="http://topics.nytimes.com/"] {
	text-decoration: inherit !important;
	color: inherit !important;
}
}
