style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(facebook.com), domain(www.facebook.com) {
.fbfPopup{
  color:#ccc !important;
}
#ff-popup-pic-div, .fbfPopup{
  background:#333 !important;
}
#fbfConfigControls, #fbfConfigTabs div, .fbfConfigSelectedControl{
  background:#222 !important;
}
.fbfConfigSelectedTab, .fbfConfigSelectedControl{
  color:#fff !important;
}
#fbf-bookmark-list, #fbf-bookmark-list li, #ff-add-bookmark, #ff-manage-bookmark{
  color:#aaa !important;
  background:#333 !important;
}
#fbf-bookmark-list{
  border-color: #000 !important;
}
#fbf-bookmark-list li:hover{
  border-color: #000 !important;
  border:0px !important;
}
.stage, .pvs{
  background:#222 !important;
  color:#ccc !important;
}
.stage{
  border-color:#000 !important;
}
}
