style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("proboards.com") {

body { background: #222!important; }

#pagedropmenu + table[width="92%"] > tbody > tr:first-child > td:first-child { color:#ccc; }

a:link, a:visited  { text-decoration: none !important; }
a:link             { color:#9F9 !important; }
a:visited          { color:#696 !important; }
a:hover            { text-decoration:underline !important; color:#6f6 !important; }

body *[bgcolor="#0099ff"], *[bgcolor="#00ffff"] { background:#444 !important; }
*[bgcolor="#0033ff"] { background:#333 !important; }


/**** background colors ****/
.windowbg2, .titlebg, .popuptitlebg, .quote, .windowbg2 .quote table.quote *, .code, .welcomebg { 
  background-color:#333 !important; background-image:none !important; 
}
.windowbg, .catbg, .menubg, .newstitlebg, .windowbg2 .code, .windowbg2 .quote, .windowbg .quote table.quote *, #fscroller { 
  background-color:#444 !important; background-image:none !important; }

/**** font colors ****/
*[color="#005e8e"] { color: #FFF!important; }
.windowbg { color:#999!important; }
td { color:#ccc!important; }


/**** lines ****/
.quote table.quote table.quote { 
  border:solid black 2px !important; 
}

} /* end */
