style.css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul); 

menubar > menu, #bookmarks-ptf, tab { font-size-adjust: .57 !important;}

tab[selected="true"] > hbox,  tab[selected="true"] > toolbarbutton  { font-size: 13px !important; 
                                                                      color: darkblue !important;}

#urlbar { color: blue !important; font-weight: 500 !important; font-size-adjust: .56 !important;}

.searchbar-textbox { color: darkblue !important; font-weight: 500 !important; font-size-adjust: .56 !important;}

textbox , textarea { font-size: 1.2em !important; font-weight: 400 !important;}

menupopup, popup { cursor: auto !important;}

#status-bar { border: none !important; padding: 0 !important; padding-left: 4px !important; color: blue !important;
              margin: -2px !important; font-size-adjust: .57 !important; border: none !important;}

#status-bar > * , #status-bar > * > * { margin: 0 !important; margin-left: 2px !important; padding: 0 !important;}

.progress-bar { -moz-appearance: none !important; background-color: limegreen !important;
                background-image: none !important; margin-top: 2px !important; margin-bottom: 2px !important;}
