style.css:
    /* http://weblogz.ir/ */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("google.com"),
domain("google.co.ck"),
domain("google.de"),
domain("yahoo.com"),
domain("travian.ir"),
domain("travian.com"),
domain("facebook.com"),
domain("farsi1.tv"),
domain("friendfeed.com"),
domain("twitter.com"),
domain("blogger.com"),
domain("blogspot.com"),
domain("goal.com"),
domain("farya.com"),
domain("alexa.com"),
domain("userstyles.org"),
url-prefix(javascript:) {
   
    /* applies font to all generic HTML tags */
    *   {
        font-family: Tahoma, Sans !important;
       font-size:  10pt !important;
        text-decoration: none !important;
    }

    .goog-icon-list-icon-meta {
        width: 100px !important;
        height: 15px !important;
        margin-right: 10px !important;
    }


    /* GMail */
    col.xX {
        width: 83px !important;
    }
    col.yY {
        width: 171px !important;
    }

    /* minor UI tweaks */

    /* specifies width of web search results column */
    div#center_col {
        width: 80% !important;
    }
    div.s {
        max-width: 100% !important;
    }
}
