style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.forgerock.com") {
  body {
    font-size: inherit !important;
  }

  #main-block, #top-block, #menu-bar, #footer-bar {
    width: auto !important;
  }

  #top-block {
    height: auto !important;
  }

  #small-box-1, #small-box-2, #small-box-3, #small-box-4, 
  #half-top-box-left, #half-top-box-right, #half-box-left, #half-box-right
  {
    display: none; /* disabled since not in flow */
  }

  #long-top-box {
    width: 98% !important;
    overflow-x: hidden;
    display: none; /* disabled since not in flow */
  }

  #main-block {
    margin: 0 !important;
  }

  #menu-bar {
    height: auto !important;
    margin-bottom: 1ex !important;
  }

  .ticker, #footer-bar {
    display: none;
  }

  #main-column, #box_news {
    font-size: inherit !important;
    padding: 1em !important;
    min-height: inherit !important;
    margin: 0 !important;
  }

  .left-column {
    margin: 1em 1em  0 0 !important;
  }

  .right-column {
    float: left !important;
  }

  #support-rock-tab {
    right: auto !important;
  }

}
