style.css:
/*about:blank & view-source: - black -- 2010-10-09*/
@-moz-document url(about:blank){
body
{
background: black !important;
color: white !important;
}
}
@-moz-document url(about:blank),
url-prefix(view-source:) {
*
{
background: black !important;
color: grey;
}
.doctype, .start-tag, .end-tag {
color: #349999 !important;
}
.attribute-name {
color: #a0a0f0 !important;
font-weight: normal !important;
}
.attribute-value {
color: white !important;
}
.comment, .cdata, .pi {
color: green !important;
}
.error, .error > .start-tag, .error > .end-tag,
.error > .comment, .error > .cdata, .error > .doctype,
.error > .pi, .error > .entity, .error > .attribute-name,
.error > .attribute-value {
color: red !important;
background: #fff !important;
font-weight: bold !important;
}
a:link {
color: #c16000 !important;
}
}
