@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("kaskus.us"), domain("kaskus.co.id"), domain("119.110.77.3"), domain("119.110.77.4") {







/* ##################################################### */
/* ################ MODIFIABLE ELEMENTS ################ */


/* Background Settings */
/* --------------------------- */
/* Modify your background here */

body, div[id*='popup_container'], #popup_child, .popup  {
background: transparent url("http://i912.photobucket.com/albums/ac323/s4nji/Kaskus%20Aero/fbaerobg.jpg") center center fixed !important;
color:#fff !important;
font-size:13px !important; }


/* User Code
/* --------------------------- */
/* Put your own code below this line */


/* ############## MODIFIABLE ELEMENTS END ############## */
/* ##################################################### */







/*  STYLE CORE  */
/* =================================== */
/* DO NOT EDIT BEYOND THIS POINT */
/* Unless you understand CSS and what you're doing */







/* ##################################################### */
/* ################## STYLE CORE START ################# */


/* Things Removal */
/* ------------------ */
  #RightNya, #Middleisi03, #a67dc462, #MiddleBanner, #Conho01midd, #Conho01Left, #Conho01right, #Middle_blue, .tab_header, #InfoForum + div, .TextInterest, .TextInterest a, .TextInterest a:hover, .TextInterest a:visited, .TextInterest a:active, #lingForumIsifr_upleft, #lingForumIsifr_upmid, #lingForumIsifr_upright, #lingForumIsifr_downleft, #lingForumIsifr_downmid, #lingForumIsifr_downright {
display: none !important;}


/* Blue top gradient */
/* --------------------- */
  .thead, .tcat, .tfoot, .vbmenu_control, #JudulForumBlue, #Grsbawahna, #WelcomeIsi {
background: -moz-linear-gradient(top, rgba(255,255,255,0.55) 0%, rgba(255,255,255,0.1) 50%, rgba(255,255,255,0) 50%, rgba(255,255,255,0) 100%) !important; 
border: none !important;
border-radius: 2px !important; }


/* Transparent Inner BGs */
/* ----------------------------- */
.vbmenu_option, #lingForumIsifr_mid > div {
background-color: transparent !important; }


/* Copyright Text */
/* --------------------- */
  script + .smallfont:after, body > .smallfont:after{
content: "Kaskus Aero - (c)2010 s4nji" !important;
color: white !important; }


/* Aero Border */
/* --------------------- */
   .tborder, #dfFooter, #menu, #lingForumIsifr, div[id*='popup_container'], .vbmenu_popup, .vBulletin_editor {
background: rgba(255,255,255,0.16) !important;
background-clip: padding-box !important;
border: 2px solid transparent !important;
-moz-border-bottom-colors:  rgba(0,0,0,.62) #f5f8fa rgba(255,255,255,0.16) rgba(255,255,255,0.16) !important;
-moz-border-left-colors: rgba(0,0,0,.62) #f5f8fa rgba(255,255,255,0.16) rgba(255,255,255,0.16) !important;
-moz-border-right-colors: rgba(0,0,0,.62) #e3e7ea rgba(255,255,255,0.16) rgba(255,255,255,0.16) !important;
-moz-border-top-colors:  rgba(0,0,0,.62) #f5f8fa rgba(255,255,255,0.16) rgba(255,255,255,0.16) !important;
-webkit-border-bottom-colors:  rgba(0,0,0,.62) #f5f8fa rgba(255,255,255,0.16) rgba(255,255,255,0.16) !important;
-webkit-border-left-colors: rgba(0,0,0,.62) #f5f8fa rgba(255,255,255,0.16) rgba(255,255,255,0.16) !important;
-webkit-border-right-colors: rgba(0,0,0,.62) #e3e7ea rgba(255,255,255,0.16) rgba(255,255,255,0.16) !important;
-webkit-border-top-colors:  rgba(0,0,0,.62) #f5f8fa rgba(255,255,255,0.16) rgba(255,255,255,0.16) !important;
border-radius: 6px !important;
padding: 4px !important; }


/* Top Column Text Color */
/* ------------------------------ */
#tblForumBits colgroup + thead td {
color: white !important; }


/* Text Centerings */
/* --------------------- */
tr[valign*="top"] > td:first-child:not(.smallfont), .tab_list {
text-align: center !important; }


/* Inner Background */
/* --------------------- */
   .panelsurround, .panel {
border: 1px none black !important;
background: rgba(255,255,255,0) !important; }


/* Smallfont color */
/* --------------------- */
   .smallfont {
color: #434343; }



/* ========================= */
/*     Button UI Change      */
/* ========================= */

   img[src$='/buttons/collapse_tcat.gif'] {
padding-left: 15px;
height: 15px;
width: 0px;
background: url('http://i912.photobucket.com/albums/ac323/s4nji/Kaskus%20Aero/arrowaero.png') 0 -16px no-repeat }

   img[src$='/buttons/collapse_tcat_collapsed.gif'] {
padding-left: 15px;
height: 15px;
width: 0px;
background: url('http://i912.photobucket.com/albums/ac323/s4nji/Kaskus%20Aero/arrowaero.png') 0 0 no-repeat }

   img[src$='/buttons/multiquote_off.gif'] {
padding-right: 26px;
width: 0px;
height: 26px;
background-color: transparent;
background: url('http://i912.photobucket.com/albums/ac323/s4nji/Kaskus%20Aero/AeroMQ.png'); }

   img[src$='/buttons/multiquote_on.gif'] {
padding-right: 26px;
width: 0px;
height: 26px;
background-color: transparent;
background: url('http://i912.photobucket.com/albums/ac323/s4nji/Kaskus%20Aero/AeroMQ-.png'); }
/*
   a[name*='vB::QuickEdit'] {
padding-top: 26px;
padding-left: 50px;
height: 26px;
background-color: transparent;
font-size: 0px;
background: url('http://i912.photobucket.com/albums/ac323/s4nji/Kaskus%20Aero/AeroEdit.png'); }
*/
   img[src$='/buttons/quote.gif'] { 
padding-left: 70px; 
width: 0px; 
height: 26px; 
background-color: transparent; 
background: url('http://i912.photobucket.com/albums/ac323/s4nji/Kaskus%20Aero/AeroQuote.png'); }

   img[src$='/buttons/reply.gif'] {
padding-left: 110px !important;
width: 0px;
height: 26px;
background-color: transparent;
background: url('http://i912.photobucket.com/albums/ac323/s4nji/Kaskus%20Aero/AeroReply.png') 0 -25px; }

   img[src$='/buttons/quickreply.gif'] { 
padding-left: 26px; 
width: 0px; 
height: 26px; 
background-color: transparent; 
background: url('http://i912.photobucket.com/albums/ac323/s4nji/Kaskus%20Aero/AeroQR.png'); }



/* Alt1 and Alt2 */
/* --------------------- */
   .alt1, .alt1Active, .IsForumColor1, #IsForum {
font-size: 13px !important;
background-color: rgba( 255, 255, 255, 0.100 ) !important;
border: none !important;
border-radius: 3px !important; }

   .alt2, .IsForumColor2 {
font-size: 13px !important;
background-color: rgba( 255, 255, 255, 0.150 ) !important;
border: none !important;
border-radius: 3px !important; }


/* Border Manipulations */
/* -------------------------- */
   .IsForumColor1 > td, .IsForumColor2 > td {
border-color: rgba(255,255,255,0.0) rgba(255,255,255,0.1) rgba(255,255,255,0.1) rgba(255,255,255,0.0) !important; }

   td[colspan="4"] > div[style*="height: 1px"], hr, #vB_Editor_001_controls div {
background: none repeat scroll 0% 0% rgba(255,255,255,0.2) !important;
color: transparent !important; }

   #popup_child, .popup > .tborder, #vbform > .tborder, .popup {
border: none !important;
border-radius: 3px !important; }


/* Glows */
/* -----------*/
   #IsForum {
box-shadow: 0 0 10px 5px rgba(255,255,255,0.25) !important;}


/* Hidden Post Warning */
/* ------------------------ */
font[color*='White']:after, font[color*='white']:after, font[color*='Transparent']:after, font[color*='transparent']:after {
content: ' --> Hidden Post detected!!' !important;
color: #DC143C !important; }


/* Small dot below post fix */
/* --------------------------------- */
td[id*='td_post'], td[style*='-moz-use-text-color'] {
border-radius: 0px !important; }

/* ################### STYLE CORE END ################## */
/* ##################################################### */






/* ##################################################### */
/* ############### EXPERIMENTAL FEATURES ############### */


/*  EXPERIMENTAL FEATS.  */
/* =================================== */
/* This section shall contain experimental features */
/* Which means it could be unstable, and the experience may vary between users and the others */


table[width*='736px'] {
margin-left: auto !important;
margin-right: auto !important; }


/* Kaskus Homepage Glows */
/* ----------------------------*/

#InfoForum > table, #ContenForum > table {
background-color: #e8e3cf !important;
box-shadow: 0 0 7px 5px #e8e3cf;
padding: 0px !important; }

#InfoForum {
padding: 10px 0 10px 0 !important; }


/* Rounded Avatar Image Border */
/* ----------------------------------------- */

.smallfont > br + a > img {
border-radius: 3px !important; }


/* Text shadow */
/* ------------------------*/
* {
text-shadow: 1px 1px 3px rgba(0,0,0,0.5) !important; }


/* ############# EXPERIMENTAL FEATURES END ############# */
/* ##################################################### */

}
