style.css:
@-moz-document domain("twitter.com") {
	/* Reduce the left-hand margin */
	#page-container {
		margin: 4px;
	}
	/* Allow header and columns to squeeze tighter */
	#page-outer, #top-bar {
		min-width: 0px;
	}
	/* Shift the profile link left from the scrollbar */
	#top-bar .active-links {
		right: 40px;
	}
}
