style.css:
@-moz-document domain("youaredaisy.appspot.com") {

body{
	background: url(http://fotoratan.appspot.com/image/29001) 20% 80% no-repeat;
	background-size:100%;
	position: relative;
}

p{
	position: absolute;
	bottom: 0;
	text-align: center;
	width: 100%;
	margin: 0;
	filter:alpa(opacity=20);   /* IE */
	-moz-opacity:0.2;          /* Moz + FF */
	opacity:0.2;
	text-shadow: #fff 1px 1px 10px;
	 -webkit-transform: rotate(6deg);
	 -moz-transform: rotate(6deg);  
}
}
