style.css:
/* Slightly transparent popup menus & tooltips */

/* Less opacity = more transparent */


menupopup,
popup, menulist > menupopup,
.menulist-menupopup, panel[type="autocomplete-richlistbox"],
.autocomplete-history-popup, panel[type="autocomplete"], panel[type="arrow"] {
  opacity: .95 !important;
}


tooltip {
  opacity: .92 !important;
}
