style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("idg.pl") {
 textarea#comment_text{ min-height:150px !important; height:auto !important; overflow:auto !important; }
 .komentarze-form-box-nick:before{ content:"nick: "; position:absolute; left:15px; }
 .komentarze-form-box-email:before{ content:"e-mail: "; position:absolute; left:230px; }
 .komentarze-form-box-nick,
 .komentarze-form-box-email { width:150px !important; margin-left:70px !important; background-position: -54px -177px !important;}
 .komentarze-form-box-nick{ margin-left:30px !important; }
 .komentarze-form-box-nick input,
 .komentarze-form-box-email input{ width:150px !important }
 .elementKoment{ display:block !important; height:auto !important; width:auto !important; opacity:1 !important; }
 
 #komentarze-form div.info{
    background-color: #00AA00;
    border-left-color: green;
    border-left-width: 1px;
    border-radius: 3px 3px 3px 3px;
    box-shadow: 0 0 3px green;
    color: white;
    margin-bottom: 10px !important;
    padding: 13px 10px 13px 45px !important;
    background: url(data:image/png;base64,%3D) 4px 6px no-repeat #0A0;
 }
 #komentarze-form div.info:empty{ padding:0px !important; }

 /*[[animation]]*/
}
