style.css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* Change active tab color */
.tabbrowser-tab[selected="true"] > hbox,
.tabbrowser-tab[selected="true"] > .tab-close-button {
  background-color: #7FBFFF!important;
  color: #fff !important;
}

/* Change active tab color on hover */
.tabbrowser-tab[selected="true"]:hover > hbox,
.tabbrowser-tab[selected="true"]:hover > .tab-close-button {
  background-color: #7FBFFF!important;
  color: #fff !important;
}

/* Change inactive opacity */
.tabbrowser-tab:not([selected="true"]) > hbox,
.tabbrowser-tab:not([selected="true"]) > .tab-close-button {
  -moz-opacity: 1 !important;
background: none !important;
  color: #fff !important;
}

/* Change inactive opacity and color on hover */
.tabbrowser-tab:not([selected="true"]):hover > hbox,
.tabbrowser-tab:not([selected="true"]):hover > .tab-close-button {
    -moz-appearance: none !important;
    -moz-border-radius: 0 !important;
    -moz-opacity: 0.5 !important;
  background-color: #fff!important;
    -moz-opacity: 1 !important;
  color: #000!important;
}

/* Change the background image of the tab bar */
.tabbrowser-tabs {
background-image:
url("data:image/jpeg;base64,")
!important;
margin: 0px !important;
border: none !important ;
   -moz-appearance: none !important;
   }

/* Change appearence of tab's progress bar*/
tab .tab-progress{
   -moz-appearance: none !important;
   background-color: transparent !important; }
tab .progress-bar{
   background-color: rgb(170,170,255) !important;
   margin-top: 1px !important; 
   margin-bottom: -1px !important;  
  -moz-appearance: none  !important; }

/* Removing the focus rings */
.tabbrowser-tab:focus .tab-text,
/* Tab Mix Plus */
.tabbrowser-tabs[firefox2notMac] > .tabbrowser-tab:focus .tab-middle .tab-text {
   border: 1px dotted transparent !important;
}

/* Change color of spacer below tabs */
.tabs-bottom {
  background-color: #7FBFFF!important;
  height: 4px !important;
}

/* Change height of spacer below tabs */
.tabs-bottom-spacer {
  height: 0px !important;
}

/* Hide each tab's close button unless you hover over the tab */
.tab-close-button,
.tabs-closebutton 
{
display:none;
}
.tabbrowser-tabs tab:hover .tab-close-button,
.tabbrowser-tabs tab:hover .tabs-closebutton
{
display:block;
}
